body.script:
        $(document).ready(function () {
            var widthOfCatalog;
            if ($.browser.webkit) {
                widthOfCatalog = $('.center_col').width() - 20;
            } else {
                widthOfCatalog = $('.center_col').width();
            }
            $('.myTable').css('width', widthOfCatalog + 'px');
            $('#carus').css('width', $('.myTable td:first').width() - 20 + 'px');
            $('.interest_block').css('width', widthOfCatalog - 260 + 'px');
            $('.catalog_li').hover(function () {
                $('.body').css('z-index', '20');
            }, function () {
                $('.body').css('z-index', '10');
            });

            $('.catalogue_item').hover(function () {
                $('.body').css('z-index', '20');
            }, function () {
                $('.body').css('z-index', '10');
            });
            $(window).resize(function () {
                var widthOfCatalog;
                if ($.browser.webkit) {
                    widthOfCatalog = $('.center_col').width() - 20;
                } else {
                    widthOfCatalog = $('.center_col').width();
                }
                $('.myTable').css('width', widthOfCatalog + 'px');
                $('#carus').css('width', $('.myTable td:first').width() - 20 + 'px');
                $('.interest_block').css('width', widthOfCatalog - 260 + 'px');
            });
            setProdFilterValues();
        });
